package org.freda.cooper4;import org.freda.cooper4.framework.utils.SpringBeanLoader;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.boot.web.servlet.ServletComponentScan;import org.springframework.context.ApplicationContext;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;/** * * Spring Boot 启动入口(项目入口) * * Created by rally on 16-3-24. */@Configuration@EnableAutoConfiguration@ComponentScan@ServletComponentScanpublic class Application{    /**     * Main     * @param args Init     */    public static void main(String[] args)    {        final ApplicationContext applicationContext = SpringApplication.run(Application.class,args);        SpringBeanLoader.setApplicationContext(applicationContext);    }}